mapper.select:
SELECT r.role_id, r.role_name, r.description, r.is_super_admin, r.created_at, r.updated_at, r.is_deleted, r.deleted_at, (SELECT COUNT(1) FROM UserRoles ur WHERE ur.role_id = r.role_id) AS user_count FROM Roles r
AND r.role_name LIKE CONCAT('%', #{roleName}, '%')
AND r.is_super_admin = #{isSuperAdmin}
AND r.is_deleted = FALSE
ORDER BY r.created_at DESC LIMIT #{pageSize} OFFSET ${(page-1)*pageSize}

mapper.update:
UPDATE Roles
role_name = #{roleName}, description = #{description}, is_super_admin = #{isSuperAdmin}, updated_at = NOW()
WHERE role_id = #{roleId}

UPDATE Roles
SET is_deleted = TRUE, deleted_at = NOW(), deleted_by = #{deletedBy}
WHERE role_id = #{roleId}

mapper.select:
SELECT COUNT(1) FROM Roles
WHERE role_name = #{roleName}
  AND role_id != #{excludeId}
