sql.where:
is_deleted = FALSE

    AND name LIKE CONCAT('%', #{name}, '%')


    AND email LIKE CONCAT('%', #{email}, '%')


    AND status = #{status}


    AND position = #{position}


    AND created_at >= #{startDate}


    AND created_at <= #{endDate}

mapper.update:
UPDATE Users
name = #{name}, email = #{email}, phone = #{phone}, avatar_url = #{avatarUrl}, department_id = #{departmentId}, employee_id = #{employeeId}, position = #{position}, description = #{description}, status = #{status},
WHERE user_id = #{userId}

UPDATE Users 
SET is_deleted = TRUE, deleted_at = NOW(), deleted_by = #{deletedBy}
WHERE user_id = #{userId}

UPDATE Users
SET last_login_time = #{lastLoginTime}
WHERE user_id = #{userId}

UPDATE Users
SET password = #{password}
WHERE user_id = #{userId}
